import React from "react";
import ReactDOM from "react-dom";
import { CSSTransition } from "react-transition-group";

import "./styles.css";

class FadeInAndOut extends React.Component {
  constructor(...args) {
    super(...args);
  }

  handleClick = () => {
    document.getElementById("wrapper").toggle("is_open_now");
  };

  render() {
    return (
      <div id="wrapper" className="wrapper">
        <div className="nav">
          <a className="nav__icon" onClick={this.handleClick}>
            click to open more
          </a>
          <div className="nav__body">DISPLAYED TEXT</div>
        </div>
      </div>
    );
  }
}

ReactDOM.render(<FadeInAndOut />, document.getElementById("root"));
