                                            this.setState(
                                                {
                                                    errMsg:
                                                        'Links will not be opened in create mode'
                                                },
                                                () => {
                                                    setTimeout(function() {
                                                        that.setState({
                                                            errMsg: ''
                                                        })
                                                    }, 1000)
                                                }
                                            )
                                        }
                                    }}
                                    onTransformStart={() => {
                                        this.setState({ isTransforming: true })
                                    }}
                                    onTransformEnd={() => {
                                        this.setState({ isTransforming: false })
                                        let star = this.refs[eachStar.ref]
                                        let scaleX = star.scaleX(),
                                            scaleY = star.scaleY()

                                        this.setState(prevState => ({
                                            stars: prevState.stars.map(
                                                eachStar =>
                                                    eachStar.name ===
                                                    star.attrs.name
                                                        ? {
                                                              ...eachStar,
                                                              innerRadius:
                                                                  star.innerRadius() *
                                                                  star.scaleX(),
                                                              outerRadius:
                                                                  star.outerRadius() *
                                                                  star.scaleX(),
                                                              rotation: star.rotation(),
                                                              x: star.x(),
                                                              y: star.y()
                                                          }
                                                        : eachStar
                                            )
                                        }))
                                        star.setAttr('scaleX', 1)
                                        star.setAttr('scaleY', 1)
                                        this.forceUpdate()
                                    }}
                                    draggable
                                    onDragMove={() => {
                                        this.state.arrows.map(eachArrow => {
                                            if (eachArrow.from !== undefined) {
                                                if (
                                                    eachStar.name ==
                                                    eachArrow.from.attrs.name
                                                ) {
                                                    eachArrow.points = [
                                                        eachStar.x,
                                                        eachStar.y,
                                                        eachArrow.points[2],
                                                        eachArrow.points[3]
                                                    ]
                                                    this.forceUpdate()
                                                }
                                            }

                                            if (eachArrow.to !== undefined) {
                                                if (
                                                    eachStar.name ===
                                                    eachArrow.to.attrs.name
                                                ) {
                                                    eachArrow.points = [
                                                        eachArrow.points[0],
                                                        eachArrow.points[1],
                                                        eachStar.x,
                                                        eachStar.y
                                                    ]
                                                    this.forceUpdate()
                                                }
                                            }
                                        })
                                    }}
                                    onDragEnd={event => {
                                        //cannot compare by name because currentSelected might not be the same
                                        //have to use ref, which appears to be overcomplicated
                                        var shape = this.refs[eachStar.ref]

                                        this.setState(prevState => ({
                                            stars: prevState.stars.map(
                                                eachStar =>
                                                    eachStar.name ===
                                                    shape.attrs.name
                                                        ? {
                                                              ...eachStar,
                                                              x: event.target.x(),
                                                              y: event.target.y()
                                                          }
                                                        : eachStar
                                            )
                                        }))
                                    }}
                                />
                            ))}
                            {this.state.texts.map(eachText => (
                                //perhaps this.state.texts only need to contain refs?
                                //so that we only need to store the refs to get more information
                                <Text
                                    textDecoration={
                                        eachText.link ? 'underline' : ''
                                    }
                                    onTransformStart={() => {
                                        var currentText = this.refs[
                                            this.state.selectedShapeName
                                        ]
                                        currentText.setAttr(
                                            'lastRotation',
                                            currentText.rotation()
                                        )
                                    }}
                                    onTransform={() => {
                                        var currentText = this.refs[
                                            this.state.selectedShapeName
                                        ]

                                        currentText.setAttr(
                                            'width',
                                            currentText.width() *
                                                currentText.scaleX()
                                        )
                                        currentText.setAttr('scaleX', 1)

                                        currentText.draw()

                                        if (
                                            currentText.attrs.lastRotation !==
                                            currentText.rotation()
                                        ) {
                                            this.state.arrows.map(eachArrow => {
                                                if (
                                                    eachArrow.to &&
                                                    eachArrow.to.name() ===
                                                        currentText.name()
                                                ) {
                                                    this.setState({
                                                        errMsg:
                                                            'Rotating texts with connectors might skew things up!'
                                                    })
                                                }
                                                if (
                                                    eachArrow.from &&
                                                    eachArrow.from.name() ===
                                                        currentText.name()
                                                ) {
                                                    this.setState({
                                                        errMsg:
                                                            'Rotating texts with connectors might skew things up!'
                                                    })
                                                }
                                            })
                                        }

                                        currentText.setAttr(
                                            'lastRotation',
                                            currentText.rotation()
                                        )
                                    }}
                                    onTransformEnd={() => {
                                        var currentText = this.refs[
                                            this.state.selectedShapeName
                                        ]

                                        this.setState(prevState => ({
                                            errMsg: '',
                                            texts: prevState.texts.map(
                                                eachText =>
                                                    eachText.name ===
                                                    this.state.selectedShapeName
                                                        ? {
                                                              ...eachText,
                                                              width: currentText.width(),
                                                              rotation: currentText.rotation(),
                                                              textWidth:
                                                                  currentText.textWidth,
                                                              textHeight:
                                                                  currentText.textHeight,
                                                              x: currentText.x(),
                                                              y: currentText.y()
                                                          }
                                                        : eachText
                                            )
                                        }))
                                        currentText.setAttr('scaleX', 1)
                                        currentText.draw()
                                    }}
                                    link={eachText.link}
                                    width={eachText.width}
                                    fill={eachText.fill}
                                    name={eachText.name}
                                    ref={eachText.ref}
                                    rotation={eachText.rotation}
                                    fontFamily={eachText.fontFamily}
                                    fontSize={eachText.fontSize}
                                    x={eachText.x}
                                    y={eachText.y}
                                    text={eachText.text}
                                    draggable
                                    onDragMove={() => {
                                        this.state.arrows.map(eachArrow => {
                                            if (eachArrow.from !== undefined) {
                                                if (
                                                    eachText.name ===
                                                    eachArrow.from.attrs.name
                                                ) {
                                                    eachArrow.points = [
                                                        eachText.x,
                                                        eachText.y,
                                                        eachArrow.points[2],
                                                        eachArrow.points[3]
                                                    ]
                                                    this.forceUpdate()
                                                }
                                            }

                                            if (eachArrow.to !== undefined) {
                                                if (
                                                    eachText.name ===
                                                    eachArrow.to.attrs.name
                                                ) {
                                                    eachArrow.points = [
                                                        eachArrow.points[0],
                                                        eachArrow.points[1],
                                                        eachText.x,
                                                        eachText.y
                                                    ]
                                                    this.forceUpdate()
                                                }
                                            }
                                        })
                                    }}
                                    onDragEnd={event => {
                                        //cannot compare by name because currentSelected might not be the same
                                        //have to use ref, which appears to be overcomplicated
                                        var shape = this.refs[eachText.ref]

                                        this.setState(prevState => ({
                                            texts: prevState.texts.map(
                                                eachtext =>
                                                    eachtext.name ===
                                                    shape.attrs.name
                                                        ? {
                                                              ...eachtext,
                                                              x: event.target.x(),
                                                              y: event.target.y()
                                                          }
                                                        : eachtext
                                            )
                                        }))
                                    }}
                                    onClick={() => {
                                        var that = this
                                        if (
                                            eachText.link !== undefined &&
                                            eachText.link !== ''
                                        ) {
                                            this.setState(
                                                {
                                                    errMsg:
                                                        'Links will not be opened in create mode'
                                                },
                                                () => {
                                                    setTimeout(function() {
                                                        that.setState({
                                                            errMsg: ''
                                                        })
                                                    }, 1000)
                                                }
                                            )

                                            //var win = window.open(eachText.link, "_blank");
                                            //win.focus();
                                        }
                                    }}
                                    onDblClick={() => {
                                        // turn into textarea
                                        var stage = this.refs.graphicStage
                                        var text = stage.findOne(
                                            '.' + eachText.name
                                        )

                                        this.setState({
                                            textX: text.absolutePosition().x,
                                            textY: text.absolutePosition().y,
                                            textEditVisible: !this.state
                                                .textEditVisible,
                                            text: eachText.text,
                                            textNode: eachText,
                                            currentTextRef: eachText.ref,
                                            textareaWidth: text.textWidth,
                                            textareaHeight: text.textHeight,
                                            textareaFill: text.attrs.fill,
                                            textareaFontFamily:
                                                text.attrs.fontFamily,
                                            textareaFontSize:
                                                text.attrs.fontSize
                                        })
                                        let textarea = this.refs.textarea
                                        textarea.focus()
                                        text.hide()
                                        var transformer = stage.findOne(
                                            '.transformer'
                                        )
                                        transformer.hide()
                                        this.refs.layer2.draw()
                                    }}
                                />
                            ))}
                            {this.state.arrows.map(eachArrow => {
                                if (!eachArrow.from && !eachArrow.to) {
                                    return (
                                        <Arrow
                                            ref={eachArrow.ref}
                                            name={eachArrow.name}
                                            points={[
                                                eachArrow.points[0],
                                                eachArrow.points[1],
                                                eachArrow.points[2],
                                                eachArrow.points[3]
                                            ]}
                                            stroke={eachArrow.stroke}
                                            fill={eachArrow.fill}
                                            draggable
                                            onDragEnd={event => {
                                                //set new points to current position

                                                //usually: state => star => x & y
                                                //now: state => arrow => attr => x & y

                                                let oldPoints = [
                                                    eachArrow.points[0],
                                                    eachArrow.points[1],
                                                    eachArrow.points[2],
                                                    eachArrow.points[3]
                                                ]

                                                let shiftX = this.refs[
                                                    eachArrow.ref
                                                ].attrs.x
                                                let shiftY = this.refs[
                                                    eachArrow.ref
                                                ].attrs.y

                                                let newPoints = [
                                                    oldPoints[0] + shiftX,
                                                    oldPoints[1] + shiftY,
                                                    oldPoints[2] + shiftX,
                                                    oldPoints[3] + shiftY
                                                ]

                                                this.refs[
                                                    eachArrow.ref
                                                ].position({ x: 0, y: 0 })
                                                this.refs.layer2.draw()

                                                this.setState(prevState => ({
                                                    arrows: prevState.arrows.map(
                                                        eachArr =>
                                                            eachArr.name ===
                                                            eachArrow.name
                                                                ? {
                                                                      ...eachArr,
                                                                      points: newPoints
                                                                  }
                                                                : eachArr
                                                    )
                                                }))
                                            }}
                                        />
                                    )
                                } else if (
                                    eachArrow.name === this.state.newArrowRef &&
                                    (eachArrow.from || eachArrow.to)
                                ) {
                                    return (
                                        <Connector
                                            name={eachArrow.name}
                                            from={eachArrow.from}
                                            to={eachArrow.to}
                                            arrowEndX={this.state.arrowEndX}
                                            arrowEndY={this.state.arrowEndY}
                                            current={true}
                                            stroke={eachArrow.stroke}
                                            fill={eachArrow.fill}
                                        />
                                    )
                                } else if (eachArrow.from || eachArrow.to) {
                                    //if arrow construction is completed
                                    return (
                                        <Connector
                                            name={eachArrow.name}
                                            from={eachArrow.from}
                                            to={eachArrow.to}
                                            points={eachArrow.points}
                                            current={false}
                                            stroke={eachArrow.stroke}
                                            fill={eachArrow.fill}
                                        />
                                    )
                                }
                            })}

                            {this.state.selectedShapeName.includes('text') ? (
                                <TransformerComponent
                                    selectedShapeName={
                                        this.state.selectedShapeName
                                    }
                                />
                            ) : (
                                <TransformerComponent
                                    selectedShapeName={
                                        this.state.selectedShapeName
                                    }
                                />
                            )}
                        </Layer>

                        <Layer
                            height={window.innerHeight}
                            width={window.innerWidth}
                            ref="layer"
                        >
                            <Toolbar
                                layer={this.refs.layer2}
                                rectName={
                                    this.state.rectangles.length +
                                    1 +
                                    this.state.rectDeleteCount
                                }
                                ellipseName={
                                    this.state.ellipses.length +
                                    1 +
                                    this.state.ellipseDeleteCount
                                }
                                starName={
                                    this.state.stars.length +
                                    1 +
                                    this.state.starDeleteCount
                                }
                                textName={
                                    this.state.texts.length +
                                    1 +
                                    this.state.textDeleteCount
                                }
                                newArrowOnDragEnd={toPush => {
                                    if (toPush.from !== undefined) {
                                        //  console.log("we are making a connector");

                                        var transform = this.refs.layer2
                                            .getAbsoluteTransform()
                                            .copy()
                                        transform.invert()
                                        let uh = transform.point({
                                            x: toPush.x,
                                            y: toPush.y
                                        })
                                        toPush.x = uh.x
                                        toPush.y = uh.y

                                        var newArrow = {
                                            points: toPush.points,
                                            ref:
                                                'arrow' +
                                                (this.state.arrows.length +
                                                    1 +
                                                    this.state
                                                        .arrowDeleteCount),
                                            name:
                                                'arrow' +
                                                (this.state.arrows.length +
                                                    1 +
                                                    this.state
                                                        .arrowDeleteCount),
                                            from: toPush.from,
                                            stroke: toPush.stroke,
                                            strokeWidth: toPush.strokeWidth,
                                            fill: toPush.fill
                                        }

                                        //  console.log(newArrow);
                                        this.setState(prevState => ({
                                            arrows: [
                                                ...prevState.arrows,
                                                newArrow
                                            ],
                                            newArrowDropped: true,
                                            newArrowRef: newArrow.name,
                                            arrowEndX: toPush.x,
                                            arrowEndY: toPush.y
                                        }))
                                    } else {
                                        //  console.log("we are making just an aarrow");
                                        var transform = this.refs.layer2
                                            .getAbsoluteTransform()
                                            .copy()
                                        transform.invert()
                                        let uh = transform.point({
                                            x: toPush.x,
                                            y: toPush.y
                                        })
                                        toPush.x = uh.x
                                        toPush.y = uh.y
                                        var newArrow = {
                                            points: [
                                                toPush.x,
                                                toPush.y,
                                                toPush.x,
                                                toPush.y
                                            ],
                                            ref:
                                                'arrow' +
                                                (this.state.arrows.length +
                                                    1 +
                                                    this.state
                                                        .arrowDeleteCount),
                                            name:
                                                'arrow' +
                                                (this.state.arrows.length +
                                                    1 +
                                                    this.state
                                                        .arrowDeleteCount),
                                            from: toPush.from,
                                            stroke: toPush.stroke,
                                            strokeWidth: toPush.strokeWidth,
                                            fill: toPush.fill
                                        }

                                        this.setState(prevState => ({
                                            arrows: [
                                                ...prevState.arrows,
                                                newArrow
                                            ],
                                            newArrowDropped: true,
                                            newArrowRef: newArrow.name,
                                            arrowEndX: toPush.x,
                                            arrowEndY: toPush.y
                                        }))
                                    }

                                    //this.refs updates after forceUpdate (because arrow gets instantiated), might be risky in the future
                                    //only this.state.arrows.length because it was pushed earlier, cancelling the +1
                                }}
                                appendToRectangles={stuff => {
                                    var layer = this.refs.layer2
                                    var toPush = stuff
                                    var stage = this.refs.graphicStage
                                    var transform = this.refs.layer2
                                        .getAbsoluteTransform()
                                        .copy()
                                    transform.invert()

                                    var pos = transform.point({
                                        x: toPush.x,
                                        y: toPush.y
                                    })

                                    if (
                                        layer.attrs.x !== null ||
                                        layer.attrs.x !== undefined
                                    ) {
                                        toPush.x = pos.x
                                        toPush.y = pos.y
                                    }

                                    this.setState(prevState => ({
                                        rectangles: [
                                            ...prevState.rectangles,
                                            toPush
                                        ],
                                        selectedShapeName: toPush.name
                                    }))
                                }}
                                appendToEllipses={stuff => {
                                    var layer = this.refs.layer2
                                    var toPush = stuff
                                    var stage = this.refs.graphicStage
                                    var transform = this.refs.layer2
                                        .getAbsoluteTransform()
                                        .copy()
                                    transform.invert()

                                    var pos = transform.point({
                                        x: toPush.x,
                                        y: toPush.y
                                    })

                                    if (
                                        layer.attrs.x !== null ||
                                        layer.attrs.x !== undefined
                                    ) {
                                        toPush.x = pos.x
                                        toPush.y = pos.y
                                    }

                                    this.setState(prevState => ({
                                        ellipses: [
                                            ...prevState.ellipses,
                                            toPush
                                        ],
                                        selectedShapeName: toPush.name
                                    }))
                                }}
                                appendToStars={stuff => {
                                    var layer = this.refs.layer2
                                    var toPush = stuff
                                    var stage = this.refs.graphicStage
                                    var transform = this.refs.layer2
                                        .getAbsoluteTransform()
                                        .copy()
                                    transform.invert()

                                    var pos = transform.point({
                                        x: toPush.x,
                                        y: toPush.y
                                    })

                                    if (
                                        layer.attrs.x !== null ||
                                        layer.attrs.x !== undefined
                                    ) {
                                        toPush.x = pos.x
                                        toPush.y = pos.y
                                    }
                                    this.setState(prevState => ({
                                        stars: [...prevState.stars, toPush],
                                        selectedShapeName: toPush.name
                                    }))
                                }}
                                appendToTexts={stuff => {
                                    var layer = this.refs.layer2
                                    var toPush = stuff
                                    var stage = this.refs.graphicStage
                                    var transform = this.refs.layer2
                                        .getAbsoluteTransform()
                                        .copy()
                                    transform.invert()

                                    var pos = transform.point({
                                        x: toPush.x,
                                        y: toPush.y
                                    })

                                    if (
                                        layer.attrs.x !== null ||
                                        layer.attrs.x !== undefined
                                    ) {
                                        toPush.x = pos.x
                                        toPush.y = pos.y
                                    }

                                    this.setState(prevState => ({
                                        texts: [...prevState.texts, toPush]
                                    }))

                                    //we can also just get element by this.refs.toPush.ref

                                    //  let text = stage.findOne("." + toPush.name);
                                    let text = this.refs[toPush.ref]
                                    //this.setState({firstTimeTextEditing: true});
                                    text.fire('dblclick')
                                }}
                            />
                        </Layer>
                    </Stage>

                    <textarea
                        ref="textarea"
                        id="textarea"
                        value={this.state.text}
                        onChange={e => {
                            this.setState({
                                text: e.target.value,
                                shouldTextUpdate: false
                            })
                        }}
                        onKeyDown={e => {
                            if (e.keyCode === 13) {
                                this.setState({
                                    textEditVisible: false,
                                    shouldTextUpdate: true
                                })

                                // get the current textNode we are editing, get the name from there
                                //match name with elements in this.state.texts,
                                let node = this.refs[this.state.currentTextRef]
                                console.log(
                                    'node width before set',
                                    node.textWidth
                                )
                                let name = node.attrs.name
                                this.setState(
                                    prevState => ({
                                        selectedShapeName: name,
                                        texts: prevState.texts.map(eachText =>
                                            eachText.name === name
                                                ? {
                                                      ...eachText,
                                                      text: this.state.text
                                                  }
                                                : eachText
                                        )
                                    }),
                                    () => {
                                        this.setState(prevState => ({
                                            texts: prevState.texts.map(
                                                eachText =>
                                                    eachText.name === name
                                                        ? {
                                                              ...eachText,
                                                              textWidth:
                                                                  node.textWidth,
                                                              textHeight:
                                                                  node.textHeight
                                                          }
                                                        : eachText
                                            )
                                        }))
                                    }
                                )

                                node.show()
                                this.refs.graphicStage
                                    .findOne('.transformer')
                                    .show()
                            }
                        }}
                        onBlur={() => {
                            this.setState({
                                textEditVisible: false,
                                shouldTextUpdate: true
                            })

                            // get the current textNode we are editing, get the name from there
                            //match name with elements in this.state.texts,

                            let node = this.refs.graphicStage.findOne(
                                '.' + this.state.currentTextRef
                            )
                            let name = node.attrs.name

                            this.setState(
                                prevState => ({
                                    selectedShapeName: name,
                                    texts: prevState.texts.map(eachText =>
                                        eachText.name === name
                                            ? {
                                                  ...eachText,
                                                  text: this.state.text
                                              }
                                            : eachText
                                    )
                                }),
                                () => {
                                    this.setState(prevState => ({
                                        texts: prevState.texts.map(eachText =>
                                            eachText.name === name
                                                ? {
                                                      ...eachText,
                                                      textWidth: node.textWidth,
                                                      textHeight:
                                                          node.textHeight
                                                  }
                                                : eachText
                                        )
                                    }))
                                }
                            )
                            node.show()
                            this.refs.graphicStage
                                .findOne('.transformer')
                                .show()
                            this.refs.graphicStage.draw()
                        }}
                        style={{
                            //set position, width, height, fontSize, overflow, lineHeight, color
                            display: this.state.textEditVisible
                                ? 'block'
                                : 'none',
                            position: 'absolute',
                            top: this.state.textY + 80 + 'px',
                            left: this.state.textX + 'px',
                            width: '300px',
                            height: '300px',
                            overflow: 'hidden',
                            fontSize: this.state.textareaFontSize,
                            fontFamily: this.state.textareaFontFamily,
                            color: this.state.textareaFill,
                            border: 'none',
                            padding: '0px',
                            margin: '0px',
                            outline: 'none',
                            resize: 'none',
                            background: 'none'
                        }}
                    />
                    <div className="errMsg">{errDisplay}</div>
                    <RightToolBar
                        disablePaste={() => {
                            this.setState({ isPasteDisabled: true })
                        }}
                        selectedName={this.state.selectedShapeName}
                        stage={this.refs.graphicStage}
                        newImage={shape => {
                            this.state.rectangles.map(eachRect => {
                                if (eachRect.name === shape.attrs.name) {
                                    var index = this.state.rectangles.indexOf(
                                        eachRect
                                    )
                                    this.state.rectangles[
                                        index
                                    ].fillPatternImage =
                                        shape.attrs.fillPatternImage

                                    this.state.rectangles[index].useImage =
                                        shape.attrs.useImage
                                    this.forceUpdate()
                                }
                            })
                        }}
                        useFill={stuff => {
                            let shapeName = stuff.shape.attrs.name
                            if (shapeName.includes('rect')) {
                                if (stuff.type === 'shapeFill') {
                                    this.setState(prevState => ({
                                        rectangles: prevState.rectangles.map(
                                            eachRect =>
                                                eachRect.name === shapeName
                                                    ? {
                                                          ...eachRect,
                                                          fill: stuff.color
                                                      }
                                                    : eachRect
                                        )
                                    }))
                                } else if (stuff.type === 'strokeFill') {
                                    this.setState(prevState => ({
                                        rectangles: prevState.rectangles.map(
                                            eachRect =>
                                                eachRect.name === shapeName
                                                    ? {
                                                          ...eachRect,
                                                          stroke: stuff.color
                                                      }
                                                    : eachRect
                                        )
                                    }))
                                }
                            }
                            if (shapeName.includes('ellipse')) {
                                if (stuff.type === 'shapeFill') {
                                    this.setState(prevState => ({
                                        ellipses: prevState.ellipses.map(
                                            eachEllipse =>
                                                eachEllipse.name === shapeName
                                                    ? {
                                                          ...eachEllipse,
                                                          fill: stuff.color
                                                      }
                                                    : eachEllipse
                                        )
                                    }))
                                } else if (stuff.type === 'strokeFill') {
                                    this.setState(prevState => ({
                                        ellipses: prevState.ellipses.map(
                                            eachEllipse =>
                                                eachEllipse.name === shapeName
                                                    ? {
                                                          ...eachEllipse,
                                                          stroke: stuff.color
                                                      }
                                                    : eachEllipse
                                        )
                                    }))
                                }
                            }
                            if (shapeName.includes('star')) {
                                if (stuff.type === 'shapeFill') {
                                    this.setState(prevState => ({
                                        stars: prevState.stars.map(eachStar =>
                                            eachStar.name === shapeName
                                                ? {
                                                      ...eachStar,
                                                      fill: stuff.color
                                                  }
                                                : eachStar
                                        )
                                    }))
                                } else if (stuff.type === 'strokeFill') {
                                    this.setState(prevState => ({
                                        stars: prevState.stars.map(eachStar =>
                                            eachStar.name === shapeName
                                                ? {
                                                      ...eachStar,
                                                      stroke: stuff.color
                                                  }
                                                : eachStar
                                        )
                                    }))
                                }
                            }
                            if (shapeName.includes('text')) {
                                this.setState(prevState => ({
                                    texts: prevState.texts.map(eachText =>
                                        eachText.name === shapeName
                                            ? {
                                                  ...eachText,
                                                  fill: stuff.color
                                              }
                                            : eachText
                                    )
                                }))
                            }
                            if (shapeName.includes('arrow')) {
                                if (stuff.type === 'shapeFill') {
                                    this.setState(prevState => ({
                                        arrows: prevState.arrows.map(eachStar =>
                                            eachStar.name === shapeName
                                                ? {
                                                      ...eachStar,
                                                      fill: stuff.color
                                                  }
                                                : eachStar
                                        )
                                    }))
                                } else if (stuff.type === 'strokeFill') {
                                    this.setState(prevState => ({
                                        arrows: prevState.arrows.map(eachStar =>
                                            eachStar.name === shapeName
                                                ? {
                                                      ...eachStar,
                                                      stroke: stuff.color
                                                  }
                                                : eachStar
                                        )
                                    }))
                                }
                            }
                        }}
                        setObjectAttr={passed => {
                            this.setState({ isPasteDisabled: false })
                            let objectName = passed.target.name()
                            if (objectName.includes('rect')) {
                                if (
                                    passed.attribute === 'link' &&
                                    passed.value !== ''
                                ) {
                                    this.setState(prevState => ({
                                        rectangles: prevState.rectangles.map(
                                            eachRect =>
                                                eachRect.name === objectName
                                                    ? {
                                                          ...eachRect,
                                                          link: passed.value
                                                      }
                                                    : eachRect
                                        )
                                    }))
                                }
                            } else if (objectName.includes('ellipse')) {
                                if (
                                    passed.attribute === 'link' &&
                                    passed.value !== ''
                                ) {
                                    this.setState(prevState => ({
                                        ellipses: prevState.ellipses.map(
                                            eachEllipse =>
                                                eachEllipse.name === objectName
                                                    ? {
                                                          ...eachEllipse,
                                                          link: passed.value
                                                      }
                                                    : eachEllipse
                                        )
                                    }))
                                }
                            } else if (objectName.includes('star')) {
                                if (
                                    passed.attribute === 'link' &&
                                    passed.value !== ''
                                ) {
                                    this.setState(prevState => ({
                                        stars: prevState.stars.map(eachStar =>
                                            eachStar.name === objectName
                                                ? {
                                                      ...eachStar,
                                                      link: passed.value
                                                  }
                                                : eachStar
                                        )
                                    }))
                                }
                            }
                        }}
                        setTextAttr={passed => {
                            //may change
                            console.log('paste abled....')
                            this.setState({ isPasteDisabled: false })

                            let text = passed.target

                            if (
                                passed.attribute === 'link' &&
                                passed.value !== ''
                            ) {
                                this.setState(prevState => ({
                                    texts: prevState.texts.map(eachText =>
                                        eachText.name === text.name()
                                            ? { ...eachText, fill: '#5ce1e6' }
                                            : eachText
                                    ),
                                    isPasteDisabled: false
                                }))
                            }

                            this.setState(prevState => ({
                                texts: prevState.texts.map(eachText =>
                                    eachText.name === text.name()
                                        ? {
                                              ...eachText,
                                              [passed.attribute]: passed.value
                                          }
                                        : eachText
                                )
                            }))
                        }}
                    />
                </div>
            </React.Fragment>
        )
    }
}
const mapStateToProps = state => ({
    auth: state.auth
})

export default connect(mapStateToProps)(withRouter(Graphics))
